// Debounce function
function debounce(fn, delay = 300) {
  let timeout;
  return function (...args) {
    clearTimeout(timeout);
    timeout = setTimeout(() => fn.apply(this, args), delay);
  };
}
function handleSection(sectionSelector, data, groupKey, isManager, isSlide) {
  const section = document.querySelector(sectionSelector);
  const gridContainer = section.querySelector(".grid-container");
  const popup = section.querySelector(isSlide ? ".popup-slide" : ".popup");
  const filterBtns = section.querySelectorAll(".filter-tab button");

  function renderPosts(filter = "all", keyword = "") {
    if (!isManager) {
      gridContainer.innerHTML = "";
    }
    let posts = [];

    const groupData = groupKey ? data[groupKey] : data;
    if (!groupData) return;

    if (groupKey) {
      groupData.children.forEach((child) => {
        if (filter === "all" || filter === child.slug) {
          posts = posts.concat(child.posts);
        }
      });
      if (keyword) {
        posts = posts.filter((post) =>
          post.title.toLowerCase().includes(keyword.toLowerCase())
        );
      }

      if (posts.length === 0) {
        gridContainer.innerHTML = "<p>No results found.</p>";
        return;
      }
    } else {
      posts = [...groupData];
    }

    posts.forEach((post) => {
      const postEl = document.createElement("div");
      if (isManager) {
        if (isSlide) {
          postEl.className = "swiper-slide";
          postEl.innerHTML = `
              <div class="card__manager">
                <img loading="lazy" data-src="${post.image}" alt="${post.title}" />
                <button class="view-more-btn">View more</button>
              </div>
            `;
        } else {
          postEl.className = "card__manager";
          postEl.innerHTML = `
                <img loading="lazy" data-src="${post.image}" alt="${post.title}" />
                <button class="view-more-btn">View more</button>
            `;
        }

      } else {
        postEl.className = "post-item";
        postEl.innerHTML = `
              <img loading="lazy" data-src="${post.image}" alt="${post.title}" />
              <h4 class="post-title">${post.title}</h4>
              <p class="post-role">${post.role}</p>
              <button class="view-more-btn">View more</button>
            `;
      }
      const viewMoreBtn = postEl.querySelector(".view-more-btn");

      viewMoreBtn.addEventListener("click", (e) => {
        const getAllPopup = document.querySelectorAll(".popup");
        getAllPopup.forEach((popup) => {
          popup.innerHTML = "";
          popup.classList.remove("show");
        });
        e.stopPropagation();
        popup.innerHTML = templatePopup(post);
        popup.classList.add("show");

        const popupClose = popup.querySelector(".popup-close");
        popupClose.addEventListener("click", () => {
          popup.classList.remove("show");
        });
      });

      gridContainer.appendChild(postEl);
    });
  }

  renderPosts();
  lazyLoadImages();

  if (groupKey) {
    filterBtns.forEach((btn) => {
      btn.addEventListener("click", () => {
        filterBtns.forEach((b) => b.classList.remove("active"));
        btn.classList.add("active");
        const filter = btn.dataset.filter;
        renderPosts(filter);
        lazyLoadImages();
      });
    });
  }
}
