const inputSearch = document.querySelector("#input__search__page");
if (inputSearch) {
  inputSearch.addEventListener(
    "input",
    debounce(() => {
      const keyword = document.querySelector("#keyword");
      const result = document.querySelector("#result");
      if (keyword) {
        keyword.textContent = inputSearch.value;
      }
      if (inputSearch.value === "") {
        gsap.to(window, {
          duration: 0.5,
          scrollTo: {
            y: ".de-group",
            offsetY:
              window.innerHeight -
              document.querySelector(".de-group").offsetHeight,
            autoKill: false,
          },
          ease: "power2.inOut",
          onComplete: () => {
            setTimeout(() => {
              result.style.display = "none";
              createScrollTrigger();
            }, 500);
          },
        });

        return;
      } else {
        // Function to remove Vietnamese accents
        function removeVietnameseAccents(str) {
          return str
            .normalize("NFD")
            .replace(/[\u0300-\u036f]/g, "")
            .replace(/đ/g, "d")
            .replace(/Đ/g, "D");
        }

        const searchTerm = inputSearch.value.toLowerCase().trim();
        const searchTermNoAccent = removeVietnameseAccents(searchTerm);
        const searchWords = searchTermNoAccent
          .split(" ")
          .filter((word) => word.length > 0);
        const dataFilter = dataArray
          .filter((item) => {
            const titleNoAccent = removeVietnameseAccents(
              item.title.toLowerCase()
            );
            const roleNoAccent = removeVietnameseAccents(
              item.role.toLowerCase()
            );
            const contentNoAccent = item.content
              ? removeVietnameseAccents(
                item.content.replace(/<[^>]*>/g, "").toLowerCase()
              )
              : "";

            // Check if all search words are found in any field
            const matches = searchWords.map((word) => {
              return (
                titleNoAccent.includes(word) ||
                roleNoAccent.includes(word) ||
                contentNoAccent.includes(word)
              );
            });

            // Calculate relevance score based on matches
            const relevanceScore = matches.reduce(
              (acc, match) => acc + (match ? 1 : 0),
              0
            );

            return relevanceScore > 0;
          })
          .sort((a, b) => {
            const aTitleNoAccent = removeVietnameseAccents(
              a.title.toLowerCase()
            );
            const bTitleNoAccent = removeVietnameseAccents(
              b.title.toLowerCase()
            );

            // Sort by relevance score, then alphabetically by title
            const aScore = searchWords.reduce(
              (acc, word) => acc + (aTitleNoAccent.includes(word) ? 1 : 0),
              0
            );
            const bScore = searchWords.reduce(
              (acc, word) => acc + (bTitleNoAccent.includes(word) ? 1 : 0),
              0
            );

            return (
              bScore - aScore || aTitleNoAccent.localeCompare(bTitleNoAccent)
            );
          });

        if (result) {
          result.style.display = "block";
        }
        handleSection(".result-group", dataFilter);

        if (result) {
          result.style.display = "block";
        }
        handleSection(".result-group", dataFilter);
        // Recreate scroll trigger after rendering results
        createScrollTrigger();
        // Scroll to results after they're rendered
        setTimeout(() => {
          gsap.to(window, {
            duration: 0.5,
            scrollTo: {
              y: result,
              offsetY: 100, // Add some offset from the top
              autoKill: false,
            },
            ease: "power2.inOut",
          });
        }, 50); // Small delay to ensure content is rendered
        return;
      }
    }, 500)
  );
}

